var images=document.getElementsByClassName("image-blur");var imageDataLarge=[];for(var i=0;i<images.length;i++){imageDataLarge.push(images[i].getAttribute("data-large"))}
if(!Modernizr.cssfilters){for(var i=0;i<images.length;i++){images[i].src="";images[i].setAttribute("data-large",null);images[i].classList.add("no-blur");images[i].classList.remove("image-blur")}}
function fetchImage(url,image){if(!Modernizr.fetch){var xhr=new XMLHttpRequest();xhr.open("GET",url,!0);xhr.onload=function(){if(this.status===200){image.src=url;image.classList.add("blur-out");image.classList.remove("image-blur")}};xhr.send()}
else{fetch(url,{mode:"no-cors"}).then(function(){image.src=url;image.classList.add("blur-out");image.classList.remove("image-blur")})}}
window.onload=function(){for(var i=0;i<images.length;i++){fetchImage(imageDataLarge[i],images[i])}}
